                id="search-users"
              />
              <label class="custom-control-label" :for="'search-users'"
                >Users <img src="~assets/imgs/search_user.png" width="25" alt=""
              /></label>
            </div>
            <div class="custom-control custom-checkbox filter-input mt-2">
              <input
                type="checkbox"
                class="custom-control-input"
                name="account_type[]"
                value="brand"
                v-model="account_type"
                id="search-brand"
              />
              <label class="custom-control-label" :for="'search-brand'"
                >Brands <img src="~assets/imgs/search_brand.png" width="25" alt=""
              /></label>
            </div>
            <div class="custom-control custom-checkbox filter-input mt-2">
              <input
                type="checkbox"
                class="custom-control-input"
                name="account_type[]"
                value="delivery"
                v-model="account_type"
                id="search-delivery"
              />
              <label class="custom-control-label" :for="'search-delivery'"
                >Delivery <img src="~assets/imgs/delivery.png" width="25" alt=""
              /></label>
            </div>
            <div class="custom-control custom-checkbox filter-input mt-2">
              <input
                type="checkbox"
                class="custom-control-input"
                name="account_type[]"
                value="storefront"
                v-model="account_type"
                id="search-storefront"
              />
              <label class="custom-control-label" :for="'search-storefront'"
                >Storefront <img src="~assets/imgs/dispensary.png" width="25" alt=""
              /></label>
            </div>
          </div>
          <div
            class="form-group floating-label mt-5"
            v-show="account_type.includes('storefront') || account_type.includes('delivery')"
          >
            <select class="form-control floating-select mt-2" id="search_distance" v-model="distance">
              <option value="" hidden></option>
              <option value="15">&lt; 15 Miles</option>
              <option value="30">&lt; 30 Miles</option>
              <option value="100">&lt; 100 Miles</option>
              <option value="10000">To the Moon</option>
            </select>
            <label for="search_distance" :class="{ focused: distance }">Distance</label>
          </div>
          <div class="form-group text-center mt-4">
            <span class="text-420" id="reset_option" @click="resetSearchOption()">Reset Options</span>
          </div>
        </form>
      </div>
    </vs-sidebar>

    <div class="col-12 text-center posts">
      <div class="search">
        <img src="~assets/imgs/search_option.png" class="search_option" alt @click="active = !active" />
        <div class="input-group mb-3">
          <div class="input-group-prepend" @click="getfollowingposts()">
            <span class="input-group-text">
              <fa icon="user-plus" fixed-width />
            </span>
          </div>
          <input
            type="text"
            class="form-control search_weedgram"
            ref="search_weedgram"
            placeholder="Search"
            @keypress="keywordChange($event)"
            @focus="hideFooter()"
            @blur="showFooter()"
            v-model="keyword"
          />
          <div class="input-group-prepend" @click="getbookmarked()">
            <span class="input-group-text">
              <fa icon="bookmark" fixed-width />
            </span>
          </div>
        </div>
      </div>
      <div class="container" style="margin-bottom: 60px">
        <div class="row justify-content-center container-defaultpost" v-if="defaultpost != null">
          <div class="col-4 media_container">
            <router-link
              :to="{
                name: 'weedgram',
                hash: `#1`,
                params: { allpost: weedgram_posts, start_index: 1, keyword: keyword, page: page },
              }"
            >
              <div class="media">
                <img v-bind:src="serverUrl(defaultpost.url)" alt />
              </div>
            </router-link>
          </div>
        </div>
        <div class="row">
          <div class="col-4 media_container" v-for="(item, index) in posts" :key="index">
            <div class="media">
              <router-link
                :to="{
                  name: 'weedgram',
                  hash: `#${index + 2}`,
                  params: {
                    allpost: weedgram_posts,
                    start_index: index + 2,
                    keyword: keyword,
                    following: following,
                    bookmark: bookmark,
                    page: page,
                  },
                }"
              >
                <img v-bind:src="serverUrl(item.url)" alt v-if="item.type == 'image'" />
                <img v-bind:src="getPosterUrl(item.url)" alt v-if="item.type == 'video' && $device.isIos" />
                <video v-bind:src="serverUrl(item.url)" alt v-if="item.type == 'video' && $device.isAndroid"></video>
                <img
                  class="video__tag__mobile"
                  v-if="item.type === 'video'"
                  src="https://i.imgur.com/88aBgwi.png"
                  alt=""
                />
              </router-link>
            </div>
          </div>
        </div>
        <infinite-loading :identifier="infiniteId" :distance="300" spinner="spiral" @infinite="getallposts"
          ><div slot="no-more"></div
        ></infinite-loading>
      </div>
    </div>
  </div>
</template>

<script>
import { mapGetters } from 'vuex'
import _ from 'lodash'

export default {
  name: 'MobileHome',
  data() {
    return {
      keyword: '',
      posts: [],
      defaultpost: null,
      active_index: this.activeindex,
      active: false,
      bookmark: false,
      following: false,
      weedgram_posts: [],
      loading: false,
      page: 1,
      infiniteId: +new Date(),
      is_last: false,
      account_type: [],
      distance: '',
      latitude: 0,
      longitude: 0,
    }
  },
  watch: {
    // call again the method if the route changes
    // '$route': 'getallposts',
    // keyword: function (newKeyword, oldKeyword) {
    //         this.debouncedGetPosts();
    //     }
  },
  created: function () {
    // this.debouncedGetPosts = _.debounce(this.keywordChange, 500);
  },
  computed: mapGetters({
    user: 'auth/user',
  }),
  mounted() {
    // this.getallposts();
    // this.scroll();
    this.changeDistance()
  },
  methods: {
    getallposts($state) {
      if (this.following || this.bookmark) {
        this.keyword = ''
      }
      let uri = '/post/allhomepost'
      let params = {
        keyword: this.keyword,
        bookmark: this.bookmark,
        following: this.following,
        account_type: this.account_type,
        distance: this.distance,
        latitude: this.latitude,
        longitude: this.longitude,
        per_page: 9,
        page: this.page,
      }
      this.loading = true
      if (this.page == 1) {
        this.posts = []
      }
      this.axios.post(uri, params).then((response) => {
        this.defaultpost = response.data.default
        if (response.data.allposts.data.length) {
          if (this.page == 1) {
            this.posts = []
          }
          if (this.posts.length == 0) {
            this.posts = response.data.allposts.data
            this.weedgram_posts.push(response.data.default)
            this.selected = this.defaultpost
          } else {
            this.posts = _.concat(this.posts, response.data.allposts.data)
          }

          response.data.allposts.data.forEach((element) => {
            this.weedgram_posts.push(element)
          })

          this.page++
          this.loading = false
          $state.loaded()
        } else {
          this.is_last = true
          $state.complete()
        }
      })
    },
    getbookmarked() {
      if (this.user) {
        this.following = false
        this.bookmark = true
        this.keywordChange('bookmark')
      } else {
        $('#loginmodal').modal('show')
      }
    },

    getfollowingposts() {
      if (this.user) {
        this.bookmark = false
        this.following = true
        this.keywordChange('following')
      } else {
        $('#loginmodal').modal('show')
      }
    },
    keywordChange(e) {
      if (e == 'search' || e == 'bookmark' || e == 'following' || e.keyCode == 13) {
        if (e.keyCode) {
          this.bookmark = this.following = false
        }
        this.page = 1
        this.posts = []
        this.defaultpost = null
        this.weedgram_posts = []
        this.infiniteId += 1
        this.$refs.search_weedgram.blur()
        window.scrollTo({ top: 0, behavior: 'smooth' })
      }
    },
    resetSearchOption() {
      this.account_type = []
      this.distance = ''
      this.last_updated_time = null
      this.keywordChange('search')
    },
    getPosition(position) {
      this.latitude = position.coords.latitude
      this.longitude = position.coords.longitude
    },
    changeDistance() {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(this.getPosition)
      } else {
        alert('Geolocation is not supported by this browser. \n Please enable it.')
      }
    },
    showPopUp(item, index) {},
    serverUrl(item) {
      if (item.charAt(0) != '/') {
        item = '/' + item
      }
      try {
        return process.env.serverUrl + item
      } catch (error) {
        return process.env.serverUrl + 'imgs/default.png'
      }
    },
    hideFooter() {
      console.log('hide footer')
      $('#footer_bar').hide()
    },
    showFooter() {
      console.log('show footer')
      $('#footer_bar').show()
    },
    showRightSidebar(direction) {
      if (direction == 'left') {
        this.active = true
      }
    },
    getPosterUrl(url) {
      var newUrl = url.replace('/video/', '/image/')
      var pointPos = newUrl.lastIndexOf('.')
      newUrl = newUrl.substring(0, pointPos) + '.jpg'
      return process.env.serverUrl + newUrl
    },
  },
}
</script>

